import React from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import BackButton from 'components/buttons/BackButton';
import EditButton from 'components/buttons/EditButton';
import DeleteButton from 'components/buttons/DeleteButton';
import MarkAsPaidButton from 'components/buttons/MarkAsPaidButton';
import InvoiceStatus from './InvoiceStatus';

const Invoice = () => {
  const invoicesData = useSelector(({ invoices }) => invoices);
  const { id } = useParams();
  const invoice = invoicesData.find((n) => n.id === id);

  return (
    <InvoiceContainer>
      <BackButton />
      <TopContainer>
        <StatusContainer>
          <p>Status</p>
          <InvoiceStatus status={invoice.status} />
        </StatusContainer>
        <TopActionContainer>
          <EditButton />
          <DeleteButton />
          <MarkAsPaidButton />
        </TopActionContainer>
      </TopContainer>
      <InfosContainer>
        <Infos>
          <div className="id-desc">
            <p>#{invoice.id}</p>
            <h5>{invoice.description}</h5>
          </div>
          <div className="senderAddress">
            <p>{invoice.senderAddress.street && invoice.senderAddress.street}</p>
            <p>{invoice.senderAddress.city && invoice.senderAddress.city}</p>
            <p>{invoice.senderAddress.postCode && invoice.senderAddress.postCode}</p>
            <p>{invoice.senderAddress.country && invoice.senderAddress.country}</p>
          </div>
          <div className="invoiceDate">
            <h5>Invoice Date</h5>
            <p>{invoice.createdAt}</p>
          </div>
          <div className="paymentDue">
            <h5>Payment Due</h5>
            <p>{invoice.paymentDue}</p>
          </div>
          <div className="billTo">
            <h5>Bill To</h5>
            <div className="adress">
              <p>{invoice.clientAddress.street && invoice.clientAddress.street}</p>
              <p>{invoice.clientAddress.city && invoice.clientAddress.city}</p>
              <p>{invoice.clientAddress.postCode && invoice.clientAddress.postCode}</p>
              <p>{invoice.clientAddress.country && invoice.clientAddress.country}</p>
            </div>
          </div>
        </Infos>
      </InfosContainer>

      <p>{invoice.clientName}</p>
      <p>{invoice.clientEmail}</p>
    </InvoiceContainer>
  );
};

export default Invoice;

const InvoiceContainer = styled.div`
  width: 730px;
  margin: auto;
  display: flex;
  flex-direction: column;
  gap: 30px;
`;

const TopContainer = styled.div`
  width: 100%;
  height: 88px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  background: white;
  border-radius: 8px;
  -webkit-box-shadow: 0px 10px 10px -10px rgba(72, 84, 159, 0.1);
  box-shadow: 0px 10px 10px -10px rgba(72, 84, 159, 0.1);
  padding: 0 35px;
`;

const StatusContainer = styled.div`
  width: 150px;
  display: flex;
  flex-direction: row;
  gap: 12px;
  align-items: center;
`;

const TopActionContainer = styled.div`
  display: flex;
  flex-direction: row;
  gap: 8px;
`;

const InfosContainer = styled.div`
  width: 100%;
  height: 631px;
  background: white;
  border-radius: 8px;
  -webkit-box-shadow: 0px 10px 10px -10px rgba(72, 84, 159, 0.1);
  box-shadow: 0px 10px 10px -10px rgba(72, 84, 159, 0.1);
  padding: 35px;
  color: ${(props) => props.theme.colors.secondary};
`;

const Infos = styled.div`
  width: 100%;
  height: 50%;
  display: grid;
  grid-template-columns: 30% 30% 40%;
  grid-template-rows: 20% 40% 40%;
  grid-template-areas:
    'id-desc . senderAddress'
    'invoiceDate . .'
    'paymentDue . .';
  h5 {
    font-size: 16px;
    font-weight: 900;
  }
  p {
    font-weight: 900;
    font-size: 13px;
    color: red;
  }
  .id-desc {
    grid-area: id-desc;
    display: flex;
    flex-direction: column;
    gap: 15px;
    p {
      letter-spacing: -0.8px;
    }
  }
  .senderAddress {
    text-align: right;
    grid-area: senderAddress;
  }
  .invoiceDate {
    display: flex;
    flex-direction: column;
    gap: 15px;
    grid-area: invoiceDate;
  }
  .paymentDue {
    grid-area: paymentDue;
  }
  .billTo {
    display: flex;
    flex-direction: column;
    gap: 15px;
  }
`;
