/* eslint-disable  */
import React from 'react';
import deleteIcon from 'assets/icon-delete.svg';
import PropTypes from 'prop-types';
import styled from 'styled-components';

const ItemList = ({ data, handleDelete, position }) => {
  console.log(position);
  return (
    <>
      <ItemData dataType="itemName">
        <label position={position}>Item Name</label>
        <input value={position} />
      </ItemData>
      <ItemData position={position} dataType="qty">
        <label>Qty.</label>
        <input value={data.quantity} />
      </ItemData>
      <ItemData position={position} dataType="price">
        <label>Price</label>
        <input value={data.price} />
      </ItemData>
      <ItemData position={position} dataType="total">
        <label>total</label>
        <p> {data.quantity * data.price > 0 ? data.quantity * data.price : 0}</p>
      </ItemData>
      <ItemData position={position} dataType="bin" onClick={() => handleDelete(data.id)}>
        <label />
        <img src={deleteIcon} alt="" />
      </ItemData>
    </>
  );
};

export default ItemList;

const ItemEntry = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 100%;
  align-items: center;
  margin-top: 20px;
`;

const ItemData = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: ${({ dataType }) =>
    (dataType === 'itemName' && 'flex-start') ||
    (dataType === 'qty' && 'center') ||
    (dataType === 'price' && 'flex-start') ||
    (dataType === 'total' && 'flex-end')};
  width: ${({ label, dataType }) =>
    (dataType === 'itemName' && '35%') ||
    (dataType === 'qty' && '10%') ||
    (dataType === 'price' && '17%') ||
    (dataType === 'total' && '17%') ||
    (dataType === 'bin' && '5%')};
  img {
    width: 80%;
  }
  label {
    display: ${({ position }) => (position === 0 && 'inline') || (position > 0 && 'none')};
  }
  p {
    height: 40px;
    line-height: 40px;
    text-align: right;
  }
`;
