/* eslint-disable arrow-body-style */
import React, { useRef, useEffect } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';

const AuthButton = ({ userName, userImage, signIn, signOut, openMenu, closeMenu, menuState }) => {
  AuthButton.propTypes = {
    userName: PropTypes.string.isRequired,
    userImage: PropTypes.string.isRequired,
    signIn: PropTypes.func.isRequired,
    signOut: PropTypes.func.isRequired,
    openMenu: PropTypes.func.isRequired,
    closeMenu: PropTypes.func.isRequired,
    menuState: PropTypes.bool.isRequired,
  };

  const authContainer = useRef();

  const handleClick = (e) => {
    if (authContainer.current.contains(e.target)) {
      return openMenu;
    }
    return closeMenu;
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClick);
    return () => {
      document.removeEventListener('mousedown', handleClick);
    };
  }, []);

  return (
    <AuthButtonContainer>
      {userName ? (
        <UserImage onClick={openMenu}>
          <img src={userImage} alt="" />
          <LogoutMenu openMenu={menuState} onClick={signOut}>
            Log out
          </LogoutMenu>
        </UserImage>
      ) : (
        <LoginButton onClick={signIn}>Sign in</LoginButton>
      )}
    </AuthButtonContainer>
  );
};
export default AuthButton;

const AuthButtonContainer = styled.div``;

const UserImage = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin: 20px 0;
  img {
    border-radius: 50%;
    width: 60%;
  }
`;

const LoginButton = styled.button`
  width: 80px;
  border: none;
  background: none;
  color: ${(props) => props.theme.colors.white};
`;

const LogoutMenu = styled.div`
  display: ${(props) => (props.openMenu ? 'block' : 'none')};
  position: absolute;
  z-index: 100;
  background: ${(props) => props.theme.colors.white};
  width: 100px;
  height: 40px;
  text-align: center;
  line-height: 40px;
  border-radius: 10px;
  cursor: pointer;
`;
